import React, { useState } from 'react';
import './App.css';

import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';
import CheckboxMarkedCircleOutlineIcon from 'mdi-react/CheckboxMarkedCircleOutlineIcon';
import CheckboxMarkedCircleIcon from 'mdi-react/CheckboxMarkedCircleIcon';
import ProgressCheckIcon from 'mdi-react/ProgressCheckIcon';
import KanbieLogo from './assets/kanbie-logo.svg';

const maxItems = 5;

const initColumns = [
  {
    title: "To-do",
    items: [
      {
        id: 1,
        content: 'Add capability to add a card in a lane'
      },
      {
        id: 2,
        content: 'Add capability to add a card in a lane 2'
      },
      {
        id: 10,
        content: 'Add capability to add a card in a lane 3'
      },
    ]
  },
  {
    title: "Doing",
    items: [
      {
        id: 3,
        content: 'Add capability to add a card in a lane'
      },
      {
        id: 4,
        content: 'Add capability to add a card in a lane 2'
      },
    ]
  },
  {
    title: "Done",
    items: [
      {
        id: 5,
        content: 'Add capability to add a card in a lane'
      },
      {
        id: 6,
        content: 'Add capability to add a card in a lane 2'
      },
    ]
  },
];

function App() {
  const [columns, setColumns] = useState(initColumns);

  const onDragEnd = (result) => {
    const { source, destination } = result;

    if(!destination) return;

    //Delete
    if(destination.droppableId === "trash") {
      const sourceItems = columns.filter((column => column.title === source.droppableId))[0].items;
      console.log(source.index)
      return;
    }

    //Different column
    if(source.droppableId !== destination.droppableId) {
      const sourceItems = columns.filter((column => column.title === source.droppableId))[0].items;
      const destItems = columns.filter((column => column.title === destination.droppableId))[0].items;
      if(destItems.length >= 5) return;
      const [removed] = sourceItems.splice(source.index, 1);
      destItems.splice(destination.index, 0, removed);
    } else {
      //Same column
      const sourceItems = columns.filter((column => column.title === source.droppableId))[0].items;
      const [removed] = sourceItems.splice(source.index, 1);
      sourceItems.splice(destination.index, 0, removed);
    }
  }

  const onAddCard = () => {

  }

  return (
    <div>
      <div className="header-container">
        <img src={KanbieLogo} width={34} className="kanbie-logo"/>
        <div className="header">
          kanbie
        </div>
      </div>
      <div className="main-container">

        <DragDropContext
          onDragEnd={onDragEnd}
        >
          {columns.map(column => (
            <Droppable droppableId={column.title}>
              {(provided, snapshot) => (
                <div
                  {...provided.droppableProps}
                  ref={provided.innerRef}
                  style={{
                    background: snapshot.isDraggingOver ? "#400033" : "#121212",
                  }}
                  className="droppable-container"
                > 
                  <div className="column-header-container">
                    <div className="column-heading">{column.title}</div>
                    {column.title === "To-do" ? 
                      <CheckboxMarkedCircleOutlineIcon size={24} color="#fff" style={{marginBottom: "8px"}}/>
                      : (column.title === "Doing" ?
                      <ProgressCheckIcon size={24} color="#fff" style={{marginBottom: "8px"}}/>
                      : 
                      <CheckboxMarkedCircleIcon size={24} color="#fff" style={{marginBottom: "8px"}}/>
                      )
                    }

                  </div>
                  {column.items.map((item, i) => (
                    <Draggable key={item.id} draggableId={item.content + item.id} index={i}>
                      {(provided, snapshot) => (
                        <div 
                          {...provided.draggableProps}
                          {...provided.dragHandleProps}
                          ref={provided.innerRef}
                          style={{
                            userSelect: 'none',
                            backgroundColor: snapshot.isDragging ? "#272727" : "#1a1a1a",
                            ...provided.draggableProps.style
                          }}
                          className="draggable-card"
                        >
                          {item.content}
                        </div>
                      )}
                    </Draggable>
                  ))}
                  {provided.placeholder}
                </div>
              )}
            </Droppable>
          ))}
          <Droppable droppableId="trash">
            {(provided, snapshot) => (
              <div
                {...provided.droppableProps}
                ref={provided.innerRef}
                style={{
                  background: snapshot.isDraggingOver ? "#400033" : "#121212",
                }}
                className="droppable-container"
              > 
              </div>
            )}  
          </Droppable>
        </DragDropContext>
      </div>
    </div>
  );
}

export default App;
