import React, { useState } from 'react';
import './App.css';

import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

const initColumns = [
  {
    id: 1,
    title: 'To-do',
    cards: [
      {
        id: 1,
        content: 'Add capability to add a card in a lane'
      },
    ]
  },
  {
    id: 2,
    title: 'Doing',
    cards: [
      {
        id: 2,
        content: 'Move a card between the lanes'
      },
    ]
  },
  {
    id: 3,
    title: 'Done',
    cards: [
      {
        id: 3,
        content: 'Move a card between the lanes 222'
      },
    ]
  }
];

function App() {
  const [columns, setColumns] = useState(initColumns);

  return (
    <div 
      style={{ 
        display: "flex",
        justifyContent: "center",
        height: "100%"
      }}
    >
      <DragDropContext

      >
        {columns.map(column => (
          <Droppable droppableId={column.id}>
            {(provided, snapshot) => (
              <div
                {...provided.droppableProps}
                ref={provided.innerRef}
                style={{
                  background: snapshot.isDraggingOver ? 'lightblue' : 'lightgrey',
                  padding: "4px",
                  width: "200px",
                  minHeight: "400px",
                }}
              >

              </div>
            )}
          </Droppable>
        ))}
      </DragDropContext>
    </div>
  );
}

export default App;
