import React from 'react';
import './App.css';
import Board from '@lourenci/react-kanban';

const board = {
  lanes: [
    {
      id: 1,
      title: 'To-do',
      cards: [
        {
          id: 1,
          title: 'Add card',
          description: 'Add capability to add a card in a lane'
        },
      ]
    },
    {
      id: 2,
      title: 'Doing',
      cards: [
        {
          id: 2,
          title: 'Drag-n-drop support',
          description: 'Move a card between the lanes'
        },
      ]
    },
    {
      id: 3,
      title: 'Done',
      cards: [
        {
          id: 3,
          title: 'Drag-n-drop support',
          description: 'Move a card between the lanes'
        },
      ]
    }
  ]
}

function App() {
  return (
    <div>
      hi o
      <Board 
        initialBoard={board} 
        allowAddLane={false}
        allowRemoveLane={false}
        allowRemoveCard={true}
      />
    </div>
  );
}

export default App;
