import React, { useState } from 'react';
import './App.css';

import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

const initColumns = [
  {
    title: 'To-do',
    items: [
      {
        id: 1,
        content: 'Add capability to add a card in a lane'
      },
      {
        id: 10,
        content: 'Add capability to add a card in a lane 2'
      },
    ]
  },
  {
    title: 'Doing',
    items: [
      {
        id: 2,
        content: 'Move a card between the lanes'
      },
    ]
  },
  {
    title: 'Done',
    items: [
      {
        id: 3,
        content: 'Move a card between the lanes 222'
      },
    ]
  }
];

function App() {
  const [columns, setColumns] = useState(initColumns);

  const onDragEnd = (result) => {
    const { source, destination } = result;

    if(!destination) return;

    const copiedItems = columns.filter((thisColumn => thisColumn.title === source.droppableId))[0].items;
    const [removed] = copiedItems.splice(source.index, 1);
    copiedItems.splice(destination.index, 0, removed);
    console.log(copiedItems);

    setColumns({
      ...columns,
    });
  }

  return (
    <div 
      style={{ 
        display: "flex",
        justifyContent: "center",
        height: "100%"
      }}
    >
      <DragDropContext
        onDragEnd={onDragEnd}
      >
        {columns.map(column => (
          <Droppable droppableId={column.title}>
            {(provided, snapshot) => (
              <div
                {...provided.droppableProps}
                ref={provided.innerRef}
                style={{
                  background: snapshot.isDraggingOver ? 'lightblue' : 'lightgrey',
                  padding: "4px",
                  margin: "10px",
                  width: "200px",
                  minHeight: "400px",
                }}
              >
                {column.items.map((item, i) => (
                  <Draggable key={item.id} draggableId={item.content + item.id} index={i}>
                    {(provided, snapshot) => (
                      <div 
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                        ref={provided.innerRef}
                        style={{
                          userSelect: 'none',
                          padding: "16px",
                          minHeight: "50px",
                          backgroundColor: snapshot.isDragging ? '#263b4a' : '#456c86',
                          color: "white",
                          ...provided.draggableProps.style
                        }}
                      >
                        {item.content}
                      </div>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </div>
            )}
          </Droppable>
        ))}
      </DragDropContext>
    </div>
  );
}

export default App;
