import React, { useState } from 'react';
import './App.css';

import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

const initColumns = [
  {
    title: "To-do",
    items: [
      {
        id: 1,
        content: 'Add capability to add a card in a lane'
      },
      {
        id: 2,
        content: 'Add capability to add a card in a lane 2'
      },
      {
        id: 10,
        content: 'Add capability to add a card in a lane 3'
      },
    ]
  },
  {
    title: "Doing",
    items: [
      {
        id: 3,
        content: 'Add capability to add a card in a lane'
      },
      {
        id: 4,
        content: 'Add capability to add a card in a lane 2'
      },
    ]
  },
  {
    title: "Done",
    items: [
      {
        id: 5,
        content: 'Add capability to add a card in a lane'
      },
      {
        id: 6,
        content: 'Add capability to add a card in a lane 2'
      },
    ]
  },
];

function App() {
  const [columns, setColumns] = useState(initColumns);

  const onDragEnd = (result) => {
    const { source, destination } = result;

    if(!destination) return;

    //Different column
    if(source.droppableId !== destination.droppableId) {
      const sourceItems = columns.filter((column => column.title === source.droppableId))[0].items;
      const destItems = columns.filter((column => column.title === destination.droppableId))[0].items;
      const [removed] = sourceItems.splice(source.index, 1);
      destItems.splice(destination.index, 0, removed);
    } else {
      //Same column
      const sourceItems = columns.filter((column => column.title === source.droppableId))[0].items;
      const [removed] = sourceItems.splice(source.index, 1);
      sourceItems.splice(destination.index, 0, removed);
    }

    console.log(columns)
  }

  return (
    <div 
      style={{ 
        display: "flex",
        justifyContent: "center",
        height: "100%"
      }}
    >
      <DragDropContext
        onDragEnd={onDragEnd}
      >
        {columns.map(column => (
          <Droppable droppableId={column.title}>
            {(provided, snapshot) => (
              <div
                {...provided.droppableProps}
                ref={provided.innerRef}
                style={{
                  background: snapshot.isDraggingOver ? 'lightblue' : "#2b2b2b",
                  padding: "4px",
                  margin: "10px",
                }}
                className="droppable-container"
              >
                {column.items.map((item, i) => (
                  <Draggable key={item.id} draggableId={item.content + item.id} index={i}>
                    {(provided, snapshot) => (
                      <div 
                        {...provided.draggableProps}
                        {...provided.dragHandleProps}
                        ref={provided.innerRef}
                        style={{
                          userSelect: 'none',
                          backgroundColor: snapshot.isDragging ? '#263b4a' : '#272727',
                          color: "white",
                          ...provided.draggableProps.style
                        }}
                        className="draggable-card"
                      >
                        {item.content}
                      </div>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </div>
            )}
          </Droppable>
        ))}
      </DragDropContext>
    </div>
  );
}

export default App;
